    def book_taken(self):
        student =  session.query(Students).filter_by(index_no = int(self.adm_no.text())).first()
        if student:
            # Register the book
            books = bookTaken(title=self.book_title.text().upper(),adm=int(self.adm_no.text()),name=self.full_name.text().upper(),status=self.status.currentText())
